
public class Ex4_9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 5; i++) //반복하려는 문장이 단 하나일 때는, 괄호를 생략할 수 있다
			System.out.println(i); // i의 값을 출력한다
		
		for(int i = 1; i <= 5; i++)
			System.out.print(i); //print()를 쓰면 가로로 출력된다
		
		System.out.println();
	}

}
